import React, { Component } from 'react';
import "./Botonera.css";

class Botonera extends Component{
  constructor(props){
    super(props);

    this.getBotonera = this.getBotonera.bind(this);
  }

  getBotonera(){
    return this.props.botones.map((caja, index)=>(

    <button className={caja.boton}
            key={index}
            disabled = {caja.boton != "no-pulsado" ? true : false}
            onClick = {() => this.props.Se_Pulso_Boton(index)}>

      {caja.boton}
    </button>

    ));
  }
render() {
  return (
    <div id = "Botonera">
       {this.getBotonera()}
    </div>
    );
  }
}

export default Botonera;
